<?php

namespace Runalyze\Calculation\Distribution;

/**
 * Generated by hand
 */
class TimeSeriesTest extends \PHPUnit_Framework_TestCase {

	public function testSimpleArray() {
		$Dist = new TimeSeries(
			array(10, 15, 20, 15),
			array( 1,  3, 10, 13)
		);

		$this->assertEquals( array(
			10 => 1,
			15 => 5,
			20 => 7
		), $Dist->histogram() );
	}

}
