<?php

namespace Runalyze\Data\Elevation\Correction;

class Strategy_MockTester extends Strategy {
	public function canHandleData() {
		return true;
	}

	public function correctElevation() {
		$this->ElevationPoints = array(1,2,3);
	}
}

/**
 * Generated by PHPUnit_SkeletonGenerator 1.2.0 on 2014-11-09 at 16:43:43.
 */
class StrategyTest extends \PHPUnit_Framework_TestCase {

	/**
	 * @expectedException \InvalidArgumentException
	 */
	public function testEmptyArrays() {
		new Strategy_MockTester(array(), array());
	}

	/**
	 * @expectedException \InvalidArgumentException
	 */
	public function testDifferentArraySizes() {
		new Strategy_MockTester(array(1,2,3), array(1,2,3,4));
	}

	public function testCorrectConstruction() {
		new Strategy_MockTester(array(1,2,3), array(1,2,3));
	}

	public function testGetCorrectedElevation() {
		$Strategy = new Strategy_MockTester(array(1,2,3), array(1,2,3));
		$Strategy->correctElevation();

		$this->assertEquals(array(1,2,3), $Strategy->getCorrectedElevation());
	}

}
