<?php

namespace Runalyze\Model\Activity\Clothes;

/**
 * Generated by hand
 */
class SingleTest extends \PHPUnit_Framework_TestCase {

	public function testSimpleObject() {
		$Object = new Single(array(
			Single::NAME => 'T-Shirt',
			Single::SHORT_NAME => 'Shirt',
			Single::GROUP_ORDER => 3
		));

		$this->assertEquals('T-Shirt', $Object->name());
		$this->assertEquals('Shirt', $Object->shortName());
		$this->assertEquals(3, $Object->groupOrder());
	}

}
